import React, { Component } from 'react';
import Header from './Header';
import './App.css';
import Nav from './Nav'
import SearchBar from './SearchBar'
import Scores from './Scores'
import CityName from './CityName'
import handleSubmit from './HandleSubmit';




class App extends Component {
  constructor(props){
    super(props)
  this.state = {
    city: "",
    nameScore: [],
    cityScore: "",
    cityName: "",
    handleSubmit: handleSubmit()
  }
}
  resetList = () => {
    this.setState({
      city: "",
      nameScore: [],
      cityScore: "",
      cityName: ""
    })
  }

  cityChanged = (event) => {
    this.setState({
      city: event.target.value
    });
  }


  render(){
    const scoreItems = this.state.nameScore
  return (
    <div className="App">
      <Nav />
      <Header />
      <SearchBar 
      cityChanged={this.cityChanged}
      handleSubmit={this.handleSubmit}/>
      <ul className="mainList">
      <CityName cityName={this.state.cityName}/>
      {this.state.firstData === 0 ? <h2>Sorry, no records for that city.</h2> :
        scoreItems.map((value,index) => {
        return <Scores key={index}
          name={value.name}
          score={value.score_out_of_10} 
          />
      })}
      </ul>

    </div>
  );
}
}

export default App;
