import React, { Component } from 'react';
import Header from './Header';
import './App.css';
import Nav from './Nav'
import SearchBar from './SearchBar'




class App extends Component {
  constructor(props){
    super(props)
  this.state = {
    city: "",
    cities: []
  }
}

  cityChanged = (event) => {
    this.setState({
      city: event.target.value
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();
  const options =  {
    method: 'GET',
    headers: {
      "Content-Type": "application/json",
    }
  }
  let rawData;
  fetch(`https://api.teleport.org/api/cities/?search=${this.state.city}`,options)
  .then(response => {
    if(!response.ok) {
      throw new Error('Something went wrong, please try again later');
    }
    return response.json();
  })
  .then(data => {

    let searchResults = "city:search-results"
    rawData = data._embedded[searchResults[0]]._links["city:item"].href;
    return fetch(rawData,options)
  })
    .then(newResponse => {
      if(!newResponse.ok){
        throw new Error('Something went wrong, please try again later');
      }
      return newResponse.json();
    })
    .then(newData => console.log(newData))
    .catch(err => {
    this.setState({
      error: err.message
    })
  })
}

  render(){
  return (
    <div className="App">
      <Nav />
      <Header />
      <SearchBar 
      cityChanged={this.cityChanged}
      handleSubmit={this.handleSubmit}/>

    </div>
  );
}
}

export default App;
