import React, { Component } from 'react';
import './Scores.css';

class Scores extends Component {
    render(){
        return(
            <li className="listItem">
                <h3 id="name">{this.props.name}</h3>
                <h4 id= {this.props.score >= 7 ? "green": this.props.score < 4 ? "red":"score"}>
                 {this.props.score} / 10
                 </h4>
                 <p>
                 {Math.floor(this.props.score) === 1 ? 
                 <p>*</p> : Math.floor(this.props.score) === 2 ? 
                 <p>**</p> : Math.floor(this.props.score) === 3 ? 
                 <p>***</p> : Math.floor(this.props.score) === 4 ? 
                 <p>****</p> : Math.floor(this.props.score) === 5 ? 
                 <p>*****</p> : Math.floor(this.props.score) === 6 ? 
                 <p>******</p> : Math.floor(this.props.score) === 7 ? 
                 <p>*******</p> : Math.floor(this.props.score) === 8 ? 
                 <p>********</p> : Math.floor(this.props.score) === 9 ? 
                 <p>*********</p> : <p></p>}
                 </p>
            </li>
        )
    }
}

export default Scores;
