import React, { Component } from 'react';
import Header from './Header';
import './App.css';
import Nav from './Nav'
import SearchBar from './SearchBar'




class App extends Component {
  constructor(props){
    super(props)
  this.state = {
    city: "",
    cities: []
  }
}

  cityChanged = (event) => {
    this.setState({
      city: event.target.value
    });
  }

  handleSubmit = (event) => {
    let rawData;
    event.preventDefault();
  const options =  {
    method: 'GET',
    headers: {
      "Content-Type": "application/json",
    }
  }
  Promise.all([
  fetch(`https://api.teleport.org/api/cities/?search=${this.state.city}`,options),
  fetch(`https://api.teleport.org/api/cities/${rawData}`,options)
  ])
  .then(responses => {
    if(!responses.ok) {
      throw new Error('Something went wrong, please try again later');
    }
    return responses.map(response => response.json());
  })
    .then(newData => {
      rawData = newData._embedded["city:searchresults"[0]]._links["city:item"].href;
      console.log(newData)
    })
    .catch(err => {
    this.setState({
      error: err.message
    })
  })
}

  render(){
  return (
    <div className="App">
      <Nav />
      <Header />
      <SearchBar 
      cityChanged={this.cityChanged}
      handleSubmit={this.handleSubmit}/>

    </div>
  );
}
}

export default App;
