import React, { Component } from 'react';
import Header from './Header';
import './App.css';
import Nav from './Nav'
import SearchBar from './SearchBar'




class App extends Component {
  constructor(props){
    super(props)
  this.state = {
    city: "",
    cities: []
  }
}

  cityChanged = (event) => {
    this.setState({
      city: event.target.value
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();
  let url = `https://api.teleport.org/api/cities/?search=${this.state.city}`;
  const options =  {
    method: 'GET',
    headers: {
      "Content-Type": "application/json",
    }
  }

  fetch(url,options)
  .then(response => {
    if(!response.ok) {
      throw new Error('Something went wrong, please try again later');
    }
    return response.json();
  })
  .then(data => console.log(data))
  .catch(err => {
    this.setState({
      error: err.message
    })
  })
}

  render(){
  return (
    <div className="App">
      <Nav />
      <Header />
      <SearchBar 
      cityChanged={this.cityChanged}
      handleSubmit={this.handleSubmit}/>
      <a class="teleport-widget-link" href="https://teleport.org/cities/aarhus/">Life quality score - Aarhus</a><script async class="teleport-widget-script" data-url="https://teleport.org/cities/aarhus/widget/scores/?currency=USD" data-max-width="770" data-height="977" src="https://teleport.org/assets/firefly/widget-snippet.min.js"></script>
    </div>
  );
}
}

export default App;
